import React, { Component } from 'react';
import axios from 'axios';
import IPData from 'ipdata';
import { Map, GoogleApiWrapper } from 'google-maps-react';

import { ipdataKey } from '../utils';
import { mapsKey } from '../utils';

import './map.css';


class Mapa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            city: '',
            country: '',
            lat: '',
            long: ''
        }
    }


    componentDidMount() {

        const IPdata = new IPData(`${ipdataKey}`)

        // axios.get(`https://api.ipdata.co/?api-key=${ipdataKey}`)
        //     .then(res => {
        //         const data = res.data;
        //         this.setState({
        //             city: data['city'],
        //             country: data['country_name'],
        //             lat: data['latitude'],
        //             long: data.longitude
        //         })
        //         console.log(res);
        //     })
        //     .catch(err => {
        //         console.log(err)
        //     })
        IPdata.lookup()
            .then(info => {
                console.log(info)
                this.setState({
                    city: info['city'],
                    country: info['country_name'],
                    lat: info['latitude'],
                    long: info.longitude
                })
            })
    }


    render() {
        console.log(this.state);
        return (
            <div className="mapContainer">
                <div>You are in {this.state.city}</div>
                <div>{this.state.country}</div>
                <div className='actual_map'>
                    <Map
                    className='map'
                    google={this.props.google}
                    zoom={10}
                    initialCenter={{ lat: `${this.state.lat}`, lng: `${this.state.long}`}}
                    />
                </div>
            </div>
        )
    }
}

export default GoogleApiWrapper({
    apiKey: mapsKey
  })(Mapa);;
