import React, { Component } from 'react';
import axios from 'axios';

import { key } from '../utils';


class Map extends Component {
    constructor(props) {
        super(props);
        this.state = {
            city: '',
            country: ''
        }
    }


    componentDidMount() {

        axios.get(`https://api.ipdata.co/?api-key=${key}`)
            .then(res => {
                const data = res.data;
                this.setState({ city: data['city'], country: data['country_name'] })
                console.log(res);
            })
    }


    render() {
        console.log(this.state);
        return (
            <div>
                <h1>This is the map</h1>
                <div>You are in {this.state.city}</div>
                <div>{this.state.country}</div>
            </div>
        )
    }
}

export default Map;
