import React, { Component } from 'react';
import axios from 'axios';

import { ipdataKey } from '../utils';
import { mapsKey } from '../utils';

import './map.css';


class Map extends Component {
    constructor(props) {
        super(props);
        this.state = {
            city: '',
            country: '',
            lat: '',
            long: ''
        }
    }


    componentDidMount() {

        axios.get(`https://api.ipdata.co/?api-key=${ipdataKey}`)
            .then(res => {
                const data = res.data;
                this.setState({
                    city: data['city'],
                    country: data['country_name'],
                    lat: data['latitude'],
                    long: data.longitude
                })
                // console.log(res);
            })
    }


    render() {
        
        return (
            <div>
                <h1>This is the map</h1>
                <div>You are in {this.state.city}</div>
                <div>{this.state.country}</div>
                <div className='actual_map'>
                    <iframe
                        width="100%"
                        height="100%"
                        frameBorder="0"
                        src={`https://www.google.com/maps/embed/v1/place?key=${mapsKey}
    &q=${this.state.city},${this.state.country}`} allowFullScreen>
                    </iframe>
                </div>
            </div>
        )
    }
}

export default Map;
