import React, { Component } from 'react';
import axios from 'axios';
import IPData from 'ipdata';

import { ipdataKey } from '../utils';
import { mapsKey } from '../utils';

import './map.css';


class Mapa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            city: '',
            country: '',
            lat: '',
            long: ''
        }
    }


    componentDidMount() {

        const IPdata = new IPData(`${ipdataKey}`)

        // axios.get(`https://api.ipdata.co/?api-key=${ipdataKey}`)
        //     .then(res => {
        //         const data = res.data;
        //         this.setState({
        //             city: data['city'],
        //             country: data['country_name'],
        //             lat: data['latitude'],
        //             long: data.longitude
        //         })
        //         console.log(res);
        //     })
        //     .catch(err => {
        //         console.log(err)
        //     })
        IPdata.lookup()
            .then(info => {
                console.log(info)
                this.setState({
                    city: info['city'],
                    country: info['country_name'],
                    lat: info['latitude'],
                    long: info.longitude
                })
            })
    }


    render() {
        console.log(this.state);
        return (
            <div>
                <div>You are in {this.state.city}</div>
                <div>{this.state.country}</div>
                <div className='actual_map'>
                    <iframe
                        width="100%"
                        height="100%"
                        frameBorder="0"
                        src={`https://www.google.com/maps/embed/v1/place?key=${mapsKey}
    &q=${this.state.lat},${this.state.long}`}
                        allowFullScreen>
                    </iframe>
                </div>
            </div>
        )
    }
}

export default Mapa;
