import React, { Component } from 'react';
import './App.css';

import MapComponent from './map/map';

class App extends Component {

  componentDidMount() {
    axios.get(`https://api.ipdata.co/?api-key=${ipdataKey}`)
            .then(res => {
                const data = res.data;
                this.setState({
                    city: data['city'],
                    country: data['country_name'],
                    lat: data['latitude'],
                    long: data.longitude
                });
            })
            .catch(err => {
                console.log(err);
                this.setState({
                    city: 'Somewhere',
                    country: 'Anywhere',
                    lat: '29.903909',
                    long: '34.999227'
                })
            })
  }



  render() {return (
    <div className="App">
      <MapComponent className="map_component"/>
    </div>
  );}
}

export default App;
