import React, { Component } from 'react';
import axios from 'axios';

import { ipdataKey } from '../utils';
import { mapsKey } from '../utils';

import './map.css';


class MapComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            city: '',
            country: '',
            lat: '',
            long: '',
            fact: ''
        }
    }


    componentDidMount() {

        axios.get(`https://api.ipdata.co/?api-key=${ipdataKey}`)
            .then(res => {
                console.log(res);
                const data = res.data;
                this.setState({
                    city: data['city'],
                    country: data['country_name'],
                    lat: data['latitude'],
                    long: data.longitude
                })
                console.log(res);
            })
            .catch(err => {
                console.log(err);
                this.setState({
                    city: 'Somewhere',
                    country: 'Anywhere',
                    lat: '29.903909',
                    long: '34.999227'
                })
            })
    }


    render() {
        
        const fact = this.state.city === 'Hidden Hills'
            ? 'Yay! Right next to Kanye'
            : this.state.city === 'Somewhere'
                ? 'It seems like something went wrong...'
                : 'Nope. You\'re still far from Kanye... Maybe that\'s for the best.';

        return (
            <div className="map_component">
                <header className="header">
                    {`Where... where am I?`} <br />
                    <div>{'Well, you\'re in '}
                        <span>{`${this.state.city}, ${this.state.country}`}</span>
                    </div>
                </header>
                <div className="map">
                    <iframe
                        style={{ width: '100vw', height: '100vh' }}
                        frameBorder="0"
                        src={`https://www.google.com/maps/embed/v1/place?key=${mapsKey}
                    &q=${this.state.lat},${this.state.long}`} allowFullScreen>
                    </iframe>
                </div>
                <section className="footer">{
                    fact
                }</section>
            </div>
        )
    }
}

export default MapComponent;
